import { useState, useEffect } from "react";
import axios from "axios";

export default function ManageCourses({ courses, onDelete, onEdit }) {
  const [localCourses, setLocalCourses] = useState(courses);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    setLocalCourses(courses);
  }, [courses]);

  const filteredCourses = localCourses.filter(course =>
    course.Title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="manage-courses">
      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Search courses..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: '8px', width: '200px' }}
        />
      </div>

      {filteredCourses.length === 0 ? (
        <p>No courses found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Title</th>
              <th>Category</th>
              <th>Cost</th>
              <th>Rating</th>
              <th>Last Updated</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredCourses.map(course => (
              <tr key={course.courseId}>
                <td>{course.Title}</td>
                <td>{course.Category}</td>
                <td>{course.Cost}</td>
                <td>{course.Rating}</td>
                <td>{course.LastUpdatedOn}</td>
                <td>
                  <div style={{ display: 'flex', gap: '10px' }}>
                    <button onClick={() => onEdit(course)}>Edit</button>
                    <button onClick={() => onDelete(course.courseId, course.userId)}>Delete</button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
