import { useState, useEffect } from "react";
import axios from "axios"; // ✅ added axios
export default function ManageUsers({ users, onDelete, onEdit }) {
  const [localUsers, setLocalUsers] = useState(users);
  const [showActionsFor, setShowActionsFor] = useState(null);
  useEffect(() => {
    setLocalUsers(users);
  }, [users]);
  // ✅ Function to call backend to update approval
  const handleApproveReject = async (email, status) => {
    try {
      // PATCH request to backend
     await axios.patch(`http://localhost:3001/users/${email.toLowerCase()}/approval`, {
  isApproved: status,
});


      // Update local UI
      const updated = localUsers.map(user =>
        user.email === email ? { ...user, isApproved: status } : user
      );
      setLocalUsers(updated);
      setShowActionsFor(null);
    } catch (err) {
      alert("❌ Failed to update approval status.");
      console.error(err);
    }
  };
  return (
    <div className="manage-users">
      {localUsers.length === 0 ? (
        <p>No users found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Category</th>
              <th>Email Verified</th>
              <th>Approval Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {localUsers.map(user => (
              <tr key={user.email}>
                <td>{user.firstName} {user.lastName}</td>
                <td>{user.email}</td>
                <td>{user.category}</td>
                <td>{user.emailVerified ? 'Yes' : 'No'}</td>
                <td>
                  {/* Show Approved or Rejected */}
                  {user.isApproved === "approved" && "✅ Approved"}
                  {user.isApproved === "rejected" && "❌ Rejected"}
                  {/* Show Pending as clickable */}
                  {user.isApproved === "pending" && showActionsFor !== user.email && (
                    <button onClick={() => setShowActionsFor(user.email)}>
                      ⏳ Pending
                    </button>
                  )}
                  {/* Show Approve/Reject buttons */}
                  {user.isApproved === "pending" && showActionsFor === user.email && (
                    <div style={{ marginTop: '5px' }}>
                      <button onClick={() => handleApproveReject(user.email, "approved")}>
                        ✅ Approve
                      </button>{" "}
                      <button onClick={() => handleApproveReject(user.email, "rejected")}>
                        ❌ Reject
                      </button>
                    </div>
                  )}
                </td>
                <td>
                  <button onClick={() => onEdit(user)}>Edit</button>
                  <button onClick={() => onDelete(user.email)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
